mapper.update:
update t_sys_user set
usercode=#{usercode},username=#{username},deptid=#{dept.deptid},password=#{password},email=#{email},
postid=#{postid},plevelid=#{plevelid},sex=#{sex},birthday=#{birthday},cellphone=#{cellphone},
home_phone=#{home_phone},office_phone=#{office_phone},address=#{address},remark=#{remark},pic=#{pic},
loginIP=#{loginIP},loginDate=#{loginDate}
where userid=#{userid}

mapper.select:
select a.userid,a.usercode,a.username,a.deptid,a.password,a.email,a.postid,a.plevelid,a.sex,a.birthday, a.cellphone,a.home_phone,a.office_phone,a.address,a.remark,a.pic,a.loginIP,a.loginDate, b.deptid,b.deptcode,b.deptname,b.deptlevel from t_sys_user a join t_sys_dept b on(a.deptid=b.deptid) where a.delflag=1
and a.username like concat('%',#{username},'%')
and FIND_IN_SET(a.deptid, getChildList(#{dept.deptid}))
and a.postid=#{postid}
and a.plevelid=#{plevelid}
limit #{begin},#{rows}
